mapper.select:
select count(id)
from user
where id = #{id}

select count(id)
from user
where id = #{id}
  and name = #{name}
  and email = #{email}

mapper.update:
update user
set password=sha2(concat(#{password}, #{salt}), 256),
	salt=#{salt}
where id = #{id}

mapper.delete:
delete
from interest
where id = #{id}
  and dongCode = #{dongcode}
